package com.victor.util;import android.text.TextUtils;import java.text.SimpleDateFormat;import java.util.Locale;/** * Created by victor on 2016/7/25. */public class DateUtil {    /*     * 毫秒转化时分秒毫秒     */    public static String formatTime(Long ms) {        Integer ss = 1000;        Integer mi = ss * 60;        Integer hh = mi * 60;        Integer dd = hh * 24;        Long day = ms / dd;        Long hour = (ms - day * dd) / hh;        Long minute = (ms - day * dd - hour * hh) / mi;        Long second = (ms - day * dd - hour * hh - minute * mi) / ss;        if(hour > 0) {            return String.format("%02d:%02d:%02d",hour, minute, second);        }else{            return String.format("%02d:%02d", minute, second);        }    }    /**     * @param l     * @return     * 获取定位时间     */    public  static String getLocationTime(long l) {        SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss:sss", Locale.CHINA);        if (l <= 0l) {            l = System.currentTimeMillis();        }        return sdf == null ? "NULL" : sdf.format(l);    }}