package com.victor.util;import com.victor.data.MusicData;import java.util.Comparator;/** * Created by victor on 2016/1/9. */public class PinyinComparator implements Comparator<MusicData> {	public int compare(MusicData o1, MusicData o2) {		if (o1.sortLetters.equals("@")				|| o2.sortLetters.equals("#")) {			return -1;		} else if (o1.sortLetters.equals("#")				|| o2.sortLetters.equals("@")) {			return 1;		} else {			return o1.sortLetters.compareTo(o2.sortLetters);		}	}}